@layer components {
  .highlight-word {
    position: relative;
    font-weight: 600;
    font-style: normal;
    color: hsl(var(--primary));
    display: inline-block;
  }

  .highlight-word::before {
    content: "";
    position: absolute;
    left: -0.3em;
    right: -0.3em;
    bottom: 0.05em;
    height: 0.4em;
    background: linear-gradient(
      120deg,
      hsl(var(--primary) / 0.15) 0%,
      hsl(var(--primary) / 0.25) 50%,
      hsl(var(--primary) / 0.15) 100%
    );
